import{_ as i,c as s,o as r,ag as t}from"./chunks/framework.CmkU-fWO.js";const d=JSON.parse('{"title":"Activité brise-glace — Profils complémentaires","description":"","frontmatter":{"title":"Activité brise-glace — Profils complémentaires","aside":false},"headers":[],"relativePath":"modules/01-onedrive/00-brise-glace.md","filePath":"modules/01-onedrive/00-brise-glace.md"}'),o={name:"modules/01-onedrive/00-brise-glace.md"};function l(n,e,a,p,u,c){return r(),s("div",null,[...e[0]||(e[0]=[t('<h2 id="activite-—-profils-complementaires" tabindex="-1">Activité — Profils complémentaires <a class="header-anchor" href="#activite-—-profils-complementaires" aria-label="Permalink to &quot;Activité — Profils complémentaires&quot;">​</a></h2><p><strong>Objectif :</strong> Former des équipes équilibrées en tenant compte des forces de chacun·e</p><hr><h2 id="_1-reflexion-individuelle-3-minutes" tabindex="-1">1. Réflexion individuelle (3 minutes) <a class="header-anchor" href="#_1-reflexion-individuelle-3-minutes" aria-label="Permalink to &quot;1. Réflexion individuelle (3 minutes)&quot;">​</a></h2><p>Prenez un moment pour réfléchir à <strong>votre profil principal</strong>,<br> et éventuellement à <strong>un profil secondaire</strong>.</p><h3 id="profils-possibles" tabindex="-1">Profils possibles <a class="header-anchor" href="#profils-possibles" aria-label="Permalink to &quot;Profils possibles&quot;">​</a></h3><ul><li><p><strong>Technique</strong> (1) Comprendre comment ça fonctionne, résoudre des problèmes, manipuler des outils techniques.</p></li><li><p><strong>Organisation</strong> (2) Structurer le travail, planifier, suivre les consignes, documenter.</p></li><li><p><strong>Communication</strong> (3) Expliquer, poser des questions, coordonner le travail d’équipe.</p></li><li><p><strong>Présentation et créativité</strong> (4) Porter attention à la lisibilité, à la présentation et à la qualité visuelle.</p></li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><p>Il n’y a pas de bon ou de mauvais profil.<br> Les profils peuvent se combiner.</p></div><hr><h2 id="_2-discussion-en-petits-groupes-4–5-minutes" tabindex="-1">2. Discussion en petits groupes (4–5 minutes) <a class="header-anchor" href="#_2-discussion-en-petits-groupes-4–5-minutes" aria-label="Permalink to &quot;2. Discussion en petits groupes (4–5 minutes)&quot;">​</a></h2><p>Formez des groupes temporaires de <strong>3 ou 4 personnes</strong> autour de vous.</p><p>Chaque personne partage :</p><ul><li>son <strong>profil principal</strong></li><li>un <strong>profil qu’elle aimerait développer</strong> pendant la session</li></ul><hr><h2 id="_3-formation-des-equipes-3-minutes" tabindex="-1">3. Formation des équipes (3 minutes) <a class="header-anchor" href="#_3-formation-des-equipes-3-minutes" aria-label="Permalink to &quot;3. Formation des équipes (3 minutes)&quot;">​</a></h2><p>Formez des équipes de 3 à 4 en essayant de :</p><ul><li>représenter <strong>au moins trois profils différents</strong></li><li>privilégier la <strong>complémentarité</strong> des forces</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Trouvez-vous à l&#39;aide de signes visuels de la main (T=1, O=2, Comm=3, Créa=4) Une équipe fonctionne mieux lorsque les rôles et les forces sont variés.</p></div><hr><h2 id="a-retenir" tabindex="-1">À retenir <a class="header-anchor" href="#a-retenir" aria-label="Permalink to &quot;À retenir&quot;">​</a></h2><ul><li>Les profils ne sont <strong>pas figés</strong></li><li>Cette activité sert à partir sur de bonnes bases pour la formation de vos équipes</li></ul><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Si vous êtes satisfaits de votre équipe, allez la former immédiatement!</li><li><a href="https://livecegepfxgqc-my.sharepoint.com/:x:/g/personal/otremblay_cegepgarneau_ca/IQAvf_qftUIQS7c759lhnDgbATnT41clfv4RMZQBOutJGFw?e=1P4kXk" target="_blank" rel="noreferrer">Formation des équipes GROUPE 1</a></li><li><a href="https://livecegepfxgqc-my.sharepoint.com/:x:/g/personal/otremblay_cegepgarneau_ca/IQAKDA7_-ptNRaliAc1eEfwhAZSEAPsPbhlJ4tLNj0EVPac?e=dnexwh" target="_blank" rel="noreferrer">Formation des équipes GROUPE 2</a></li></ul></div>',22)])])}const f=i(o,[["render",l]]);export{d as __pageData,f as default};
