import{_ as r,c as s,o as i,ag as a}from"./chunks/framework.CmkU-fWO.js";const t="/cours/assets/utilisateurs.C8IqDbp7.png",o="/cours/assets/langue.D-Jglst6.png",l="/cours/assets/langue2.3EQZdDhb.png",n="/cours/assets/langue3.C5BY3eP_.png",c="/cours/assets/langue4.Ck1beI5B.png",u="/cours/assets/extensions.b9oFtc5b.png",d="/cours/assets/office.B1ptxs3y.png",p="/cours/assets/installer-office.CNYgLd0d.png",g="/cours/assets/onedrive.CijS9BGe.png",q=JSON.parse('{"title":"Installations et configurations","description":"","frontmatter":{},"headers":[],"relativePath":"modules/01-onedrive/02-installations.md","filePath":"modules/01-onedrive/02-installations.md"}'),h={name:"modules/01-onedrive/02-installations.md"};function m(f,e,v,b,k,x){return i(),s("div",null,[...e[0]||(e[0]=[a('<h1 id="installations-et-configurations" tabindex="-1">Installations et configurations <a class="header-anchor" href="#installations-et-configurations" aria-label="Permalink to &quot;Installations et configurations&quot;">​</a></h1><div class="tip custom-block"><p class="custom-block-title">Une configuration initiale correcte évite beaucoup de perte de temps (mauvaises langues de clavier, notifications manquantes, fichiers non synchronisés). Les outils installés (navigateur, suite M365, OneDrive, Outlook, Teams) seront essentiels pour votre programme d&#39;études.</p></div><div class="warning custom-block"><p class="custom-block-title">Vous devez effectuer les configurations suivantes avant de réaliser le <strong><a href="./../../labs/lab-01.html">laboratoire 1</a></strong> qui est à remettre d&#39;ici le prochain cours.</p></div><h2 id="installations-requises" tabindex="-1">Installations requises <a class="header-anchor" href="#installations-requises" aria-label="Permalink to &quot;Installations requises&quot;">​</a></h2><ul class="checklist"><li><label><input type="checkbox"><span class="check-text">Créer votre session utilisateur sur l&#39;ordinateur</span></label></li><li><label><input type="checkbox"><span class="check-text">Vérifier et configurer la langue et la disposition du clavier (Français — Canada)</span></label></li><li><label><input type="checkbox"><span class="check-text">Afficher les extensions de fichiers</span></label></li><li><label><input type="checkbox"><span class="check-text">Installer Google Chrome et ajouter vos favoris</span></label></li><li><label><input type="checkbox"><span class="check-text">Installer Teams, OneDrive et Outlook, activer notifications</span></label></li><li><label><input type="checkbox"><span class="check-text">Configurer la synchronisation OneDrive</span></label></li><li><label><input type="checkbox"><span class="check-text">Créer votre horaire dans Teams</span></label></li><li><label><input type="checkbox"><span class="check-text">Installer les applications mobiles recommandées (Outlook, Teams, OneDrive, Omnivox)</span></label></li><li><label><input type="checkbox"><span class="check-text">(Optionnel) Créer un Trello pour une gestion pratique de vos tâches</span></label></li></ul><h2 id="creer-votre-session-utilisateur-sur-l-ordinateur" tabindex="-1">Créer votre session utilisateur sur l&#39;ordinateur <a class="header-anchor" href="#creer-votre-session-utilisateur-sur-l-ordinateur" aria-label="Permalink to &quot;Créer votre session utilisateur sur l&#39;ordinateur&quot;">​</a></h2><div class="flex"><ul><li>Ouvrir <strong>Paramètres</strong> → <strong>Comptes</strong> → <strong>Autres utilisateurs</strong>.</li><li>Cliquer sur <strong>Ajouter un compte</strong>, puis suivre l&#39;assistant pour ajouter un compte.</li><li>Utilisez votre courriel .edu du Cégep ou un autre adresse courriel de votre choix.</li></ul><img src="'+t+'" alt="utilisateurs" class="img-bordered w-[30%]"></div><h2 id="verifier-et-configurer-la-langue-disposition-du-clavier" tabindex="-1">Vérifier et configurer la langue / disposition du clavier <a class="header-anchor" href="#verifier-et-configurer-la-langue-disposition-du-clavier" aria-label="Permalink to &quot;Vérifier et configurer la langue / disposition du clavier&quot;">​</a></h2><div class="flex mb-5"><ul><li>Ouvrir <strong>Paramètres</strong> → <strong>Heure et langue</strong> → <strong>Langue et région</strong>.</li><li>Ajouter <strong>Français (Canada)</strong> si nécessaire et définir la disposition de clavier correspondante.</li><li>Supprimer les langues inutiles et tester la saisie (icône langue dans la barre des tâches).</li></ul><img src="'+o+'" alt="langue" class="img-bordered w-[30%]"></div><img src="'+l+'" alt="langue 1" class="img-bordered mb-5"><img src="'+n+'" alt="langue 2" class="img-bordered mb-5"><img src="'+c+'" alt="langue 3" class="img-bordered"><h2 id="afficher-les-extensions-de-fichiers" tabindex="-1">Afficher les extensions de fichiers <a class="header-anchor" href="#afficher-les-extensions-de-fichiers" aria-label="Permalink to &quot;Afficher les extensions de fichiers&quot;">​</a></h2><ul><li>Dans <strong>Explorateur de fichiers</strong> : <strong>Affichage</strong> → activer <strong>Extensions de nom de fichier</strong> (ou dans <strong>Options</strong> → <strong>Affichage</strong>, décocher «Masquer les extensions des fichiers connus»).</li><li>Vérifier que les fichiers affichent maintenant leur extension (ex. <code>fichier.docx</code>).</li></ul><img src="'+u+'" alt="extensions" class="img-bordered"><h2 id="installer-google-chrome-et-gerer-les-favoris" tabindex="-1">Installer Google Chrome et gérer les favoris <a class="header-anchor" href="#installer-google-chrome-et-gerer-les-favoris" aria-label="Permalink to &quot;Installer Google Chrome et gérer les favoris&quot;">​</a></h2><ul><li>Télécharger Chrome : <a href="https://www.google.com/chrome" target="_blank" rel="noreferrer">https://www.google.com/chrome</a> et lancer l&#39;installateur.</li><li>Ouvrir Chrome, se connecter pour synchroniser les favoris entre appareils.</li><li><a href="https://support.google.com/chrome/answer/95417?hl=fr&amp;co=GENIE.Platform%3DDesktop#zippy=%2Cwindows" target="_blank" rel="noreferrer">Définir Chrome comme navigateur par défaut</a>.</li><li>Activer la barre des favoris (Ctrl+Shift+B) et ajouter un dossier de favoris via l&#39;étoile dans la barre d&#39;adresse.</li></ul><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Vous pouvez également opter pour un autre navigateur tel que <a href="https://www.firefox.com/" target="_blank" rel="noreferrer">Firefox</a> ou <a href="https://brave.com/" target="_blank" rel="noreferrer">Brave (pas de compte ou synchronisation)</a>.</p></div><h2 id="installer-teams-onedrive-et-outlook" tabindex="-1">Installer Teams, OneDrive et Outlook <a class="header-anchor" href="#installer-teams-onedrive-et-outlook" aria-label="Permalink to &quot;Installer Teams, OneDrive et Outlook&quot;">​</a></h2><ul><li><p>Télécharger <a href="https://office.com" target="_blank" rel="noreferrer">Microsoft 365</a></p><ul><li><img src="'+d+'" alt="office" class="img-bordered"></li><li><img src="'+p+'" alt="installer-office" class="img-bordered"></li></ul></li><li><p>Télécharger <a href="https://apps.microsoft.com/detail/xp8bt8dw290mpq?hl=fr-CA&amp;gl=CA" target="_blank" rel="noreferrer">Teams</a></p></li><li><p>Télécharger <a href="https://apps.microsoft.com/detail/9nrx63209r7b?hl=fr-CA&amp;gl=US" target="_blank" rel="noreferrer">Outlook</a></p></li><li><p>Se connecter aux applications avec votre compte Cégep .edu.</p></li><li><p>Dans <strong>Teams</strong> : Paramètres → <strong>Notifications</strong> → activer les notifications souhaitées.</p></li><li><p>Dans <strong>Outlook</strong> : vérifier que le calendrier est présent.</p></li></ul><h2 id="configurer-onedrive-synchronisation" tabindex="-1">Configurer OneDrive (synchronisation) <a class="header-anchor" href="#configurer-onedrive-synchronisation" aria-label="Permalink to &quot;Configurer OneDrive (synchronisation)&quot;">​</a></h2><div class="flex mb-5"><ul><li>Lancer OneDrive, se connecter et sélectionner les dossiers à synchroniser.</li><li>Vérifier l&#39;icône de synchronisation (en cours / coche verte) pour confirmer que tout se synchronise.</li></ul><img src="'+g+'" alt="onedrive" class="img-bordered w-[30%]"></div><h2 id="creer-votre-horaire-dans-teams" tabindex="-1">Créer votre horaire dans Teams <a class="header-anchor" href="#creer-votre-horaire-dans-teams" aria-label="Permalink to &quot;Créer votre horaire dans Teams&quot;">​</a></h2><ul><li>Ouvrir <strong>Teams</strong> → <strong>Calendrier</strong></li><li>Vous pouvez voir et ajouter des rencontres à votre calendrier.</li></ul><h2 id="installer-les-applications-mobiles-recommandees" tabindex="-1">Installer les applications mobiles recommandées <a class="header-anchor" href="#installer-les-applications-mobiles-recommandees" aria-label="Permalink to &quot;Installer les applications mobiles recommandées&quot;">​</a></h2><ul><li>Sur mobile, ouvrir <strong>App Store</strong> ou <strong>Google Play</strong>, rechercher <strong>Outlook</strong>, <strong>Teams</strong>, <strong>OneDrive</strong>, <strong>Omnivox</strong> et installer.</li><li>Se connecter et activer les notifications. (Optionnel : ajouter le widget Agenda sur l&#39;écran d&#39;accueil.)</li></ul>',26)])])}const C=r(h,[["render",m]]);export{q as __pageData,C as default};
