import{_ as r,c as i,o,ag as s}from"./chunks/framework.CmkU-fWO.js";const c="/cours/assets/gestion-fichiers.Glq567FI.png",h=JSON.parse('{"title":"Gestion des fichiers","description":"","frontmatter":{},"headers":[],"relativePath":"modules/01-onedrive/03-gestion-fichiers.md","filePath":"modules/01-onedrive/03-gestion-fichiers.md"}'),t={name:"modules/01-onedrive/03-gestion-fichiers.md"};function d(a,e,l,n,u,p){return o(),i("div",null,[...e[0]||(e[0]=[s('<h1 id="gestion-des-fichiers" tabindex="-1">Gestion des fichiers <a class="header-anchor" href="#gestion-des-fichiers" aria-label="Permalink to &quot;Gestion des fichiers&quot;">​</a></h1><h2 id="objectifs" tabindex="-1">Objectifs <a class="header-anchor" href="#objectifs" aria-label="Permalink to &quot;Objectifs&quot;">​</a></h2><ol><li>Création et renommage de dossiers/fichiers</li><li>Opérations de déplacement, copie et suppression</li><li>Archivage (.zip) et décompression</li><li>Règles de tri et conventions de nommage</li><li>Modèles de dossiers &amp; procédure de classement</li><li>Gestion des accès et synchronisation OneDrive</li></ol><h2 id="theorie-demos-en-bref" tabindex="-1">Théorie / Démos (en bref) <a class="header-anchor" href="#theorie-demos-en-bref" aria-label="Permalink to &quot;Théorie / Démos (en bref)&quot;">​</a></h2><ul><li>Créer des dossiers avec un raccourci : <code>Ctrl+Maj+N</code> (ou menu contextuel) et créer des fichiers via le clic droit → <strong>Nouveau</strong>.</li><li>Les raccourcis habituels : Copier (<code>Ctrl+C</code>), Couper (<code>Ctrl+X</code>), Coller (<code>Ctrl+V</code>).</li><li>Renommer : <code>F2</code> (ou <code>Fn+F2</code> selon l&#39;ordinateur).</li><li>Création d&#39;archives : clic droit → <strong>Envoyer vers</strong> → <strong>Dossier compressé (.zip)</strong>; pour dézipper : clic droit → <strong>Extraire tout</strong>.</li><li>Ordre de tri : caractères spéciaux → chiffres (0‑9) → lettres (A‑Z). Prévoir des préfixes numériques pour ordonner par étape.</li><li>Modèles de dossiers : créer un dossier « modèle » standard (ex. <code>Semaines</code>, <code>Travaux</code>) et copier en base pour chaque projet.</li><li>Accès : privilégier la règle du moindre privilège (donner lecture par défaut, ajouter écriture seulement si nécessaire). OneDrive propose partage en lecture seule ou en modification et permet de suivre l&#39;historique des fichiers.</li></ul><h2 id="exercice-en-classe" tabindex="-1">Exercice (en classe) <a class="header-anchor" href="#exercice-en-classe" aria-label="Permalink to &quot;Exercice (en classe)&quot;">​</a></h2><p><strong>Exercice 1 — Classement numérique</strong></p><ol><li><p>Dans votre <strong>OneDrive</strong>, créez un dossier par cours pour votre première session (ex. <code>420-06A Milieu de l&#39;info</code>).</p></li><li><p>Pour assurer un tri cohérent, <strong>préfixez</strong> le nom du dossier par le numéro du programme suivi d&#39;un séparateur et du nom du cours (ex. <code>420-06A Milieu de l&#39;info</code>).</p></li><li><p>Dans le dossier du cours (travaillez d&#39;abord sur <strong>Milieu de l&#39;info</strong>) :</p><ul><li><p>Créez un dossier pour les travaux et préfixez-le pour qu&#39;il soit classé en haut (ex. <code>01_Travaux</code>).</p></li><li><p>Créez un dossier pour les examens (ex. <code>02_Examens</code>).</p></li><li><p>Créez un dossier d&#39;archives qui doit apparaître en bas (ex. <code>99_Archives</code>) pour y déposer les fichiers anciens ou incertains.</p></li><li><p>Créez un dossier par semaine de cours (ex. <code>S01</code>, <code>S02</code>, … <code>S15</code>) ou par numéro de cours (ex. <code>Cours 1</code>, <code>Cours 2</code>, …) et vérifiez que l&#39;ordre est préservé.</p></li></ul></li><li><p>Faites une copie du dossier <code>420-06A Milieu de l&#39;info</code> et rendez la copie <strong>générique</strong> (par ex. <code>gabarit_cours</code>) pour pouvoir la réutiliser pour d&#39;autres cours.</p></li><li><p>Compressez le dossier racine (<code>420-06A Milieu de l&#39;info</code>) en <code>.zip</code>, déplacez l&#39;archive sur votre Bureau et testez la décompression pour valider l&#39;archive.</p></li></ol><img src="'+c+'" alt="gestion-fichiers" class="img-bordered"><hr>',10)])])}const f=r(t,[["render",d]]);export{h as __pageData,f as default};
