import{_ as r,c as t,o,ag as i}from"./chunks/framework.DX2MDgaT.js";const g=JSON.parse('{"title":"Collaboration et coédition","description":"","frontmatter":{},"headers":[],"relativePath":"modules/01-onedrive/04-collaboration.md","filePath":"modules/01-onedrive/04-collaboration.md"}'),n={name:"modules/01-onedrive/04-collaboration.md"};function a(s,e,l,u,c,d){return o(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="collaboration-et-coedition" tabindex="-1">Collaboration et coédition <a class="header-anchor" href="#collaboration-et-coedition" aria-label="Permalink to &quot;Collaboration et coédition&quot;">​</a></h1><h2 id="objectifs" tabindex="-1">Objectifs <a class="header-anchor" href="#objectifs" aria-label="Permalink to &quot;Objectifs&quot;">​</a></h2><ul><li>Comprendre et pratiquer la co‑édition en temps réel sur Word via OneDrive.</li><li>Utiliser le partage, les commentaires et l&#39;historique de versions pour collaborer proprement.</li><li>Produire une note structurée en équipe (astuces et procédures) avec captures d&#39;écran annotées.</li></ul><h2 id="plan-du-module" tabindex="-1">Plan du module <a class="header-anchor" href="#plan-du-module" aria-label="Permalink to &quot;Plan du module&quot;">​</a></h2><ol><li>Créer et partager un document Word dans OneDrive</li><li>Co‑édition en temps réel (Word)</li><li>Commentaires, @mentions et résolution de discussions</li><li>Historique des versions et restauration</li><li>Exercice : rédaction d&#39;astuces en équipe</li></ol><h2 id="guide-rapide-de-co‐edition" tabindex="-1">Guide rapide de co‑édition <a class="header-anchor" href="#guide-rapide-de-co‐edition" aria-label="Permalink to &quot;Guide rapide de co‑édition&quot;">​</a></h2><ul><li>Créer le document : OneDrive → dossier du cours → <strong>Nouveau</strong> → <strong>Document Word</strong>.</li><li>Partager : clic droit → <strong>Partager</strong> → entrer l&#39;adresse courriel du partenaire → définir <strong>Peut modifier</strong> → <strong>Envoyer / Copier le lien</strong>.</li><li>Co‑éditer : ouvrir le document (Word). Vous verrez les indicateurs de présence et les modifications en direct.</li><li>Commentaires : Sélectionner le texte → <strong>Nouveau commentaire</strong> → utiliser <code>@</code> pour mentionner un collègue et lui assigner une action.</li><li>Historique : Fichier → Informations → <strong>Historique des versions</strong> (ou sur OneDrive web → Version History) pour consulter/restaurer.</li></ul><h2 id="exercice-en-classe" tabindex="-1">Exercice (en classe) <a class="header-anchor" href="#exercice-en-classe" aria-label="Permalink to &quot;Exercice (en classe)&quot;">​</a></h2><p><strong>Exercice 2 — Rédaction d’astuces en équipe</strong></p><p>Objectif : produire, en équipe de 2, une note de cours structurée contenant des astuces pratiques vues pendant la séance, avec étapes et captures d&#39;écran annotées.</p><p>Étapes :</p><ol><li><p><strong>Créer le document</strong></p><ul><li>Dans votre dossier OneDrive dédié au cours (ex. <code>420-06A Milieu de l&#39;info</code>), créez un nouveau document Word nommé <code>Outils pratiques et astuces - ÉquipeX</code>.</li></ul></li><li><p><strong>Partager avec votre partenaire</strong></p><ul><li>Partagez le document en définissant <strong>Peut modifier</strong> pour le courriel du partenaire.</li><li>Vérifiez que vous pouvez co‑éditer en même temps (les curseurs / avatars apparaissent).</li></ul></li><li><p><strong>Titrez le document</strong></p><ul><li>Tapez en haut : <strong>Outils pratiques et astuces</strong> et ajoutez les noms des deux membres.</li></ul></li><li><p><strong>Sélection des sujets</strong></p><ul><li>À partir des sections pertinentes du cours d&#39;aujourd&#39;hui (gestion des fichiers / collaboration), chaque membre choisi <strong>2 astuces</strong> qu&#39;il/elle ne connaissait pas ou qu&#39;il/elle juge particulièrement utiles.</li></ul></li><li><p><strong>Pour chaque astuce (à produire par responsable)</strong></p><ul><li>Donner un <strong>titre</strong> clair.</li><li>Décrire <strong>l&#39;objectif</strong> (1–2 phrases).</li><li>Ajouter une <strong>liste numérotée</strong> décrivant les étapes (format pas à pas).</li><li>Faire une <strong>capture d&#39;écran annotée</strong> (Win+Shift+S ou Outil Capture) et l&#39;insérer dans le document avec une courte légende.</li></ul></li></ol><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Il n&#39;y a pas de remise requise pour cet exercice.</p></div><h2 id="annexe-—-creer-une-capture-d-ecran-avec-annotations" tabindex="-1">Annexe — Créer une capture d’écran avec annotations <a class="header-anchor" href="#annexe-—-creer-une-capture-d-ecran-avec-annotations" aria-label="Permalink to &quot;Annexe — Créer une capture d’écran avec annotations&quot;">​</a></h2><ol><li>Appuyez sur [Windows] + [Majuscule] + [S].</li><li>L’outil de capture s’ouvre en haut de l’écran ; sélectionnez l’outil de capture désiré (rectangulaire, découpe libre, fenêtre, plein écran).</li><li>Sélectionnez la zone à capturer ; la capture est placée dans le presse‑papier et une mini‑fenêtre de notification Outil Capture d’écran apparaît en bas à droite.</li><li>Cliquez sur la notification ou ouvrez l’<strong>Outil Capture d’écran</strong> pour <strong>annoter</strong> (dessins, flèches, texte, surlignage).</li><li>Copier (Ctrl+C) la capture annotée et collez‑la dans le document Word (Ctrl+V) ou utilisez <strong>Enregistrer sous</strong> pour sauvegarder une image à joindre.</li></ol><blockquote><p>Astuce : utilisez des couleurs contrastées pour les annotations et ajoutez une courte légende décrivant l’action mise en évidence.</p></blockquote>',16)])])}const m=r(n,[["render",a]]);export{g as __pageData,m as default};
