import{_ as s,c as t,o as i,ag as o}from"./chunks/framework.CmkU-fWO.js";const r="/cours/assets/tp1-etude-de-cas.CY_CUMtM.png",a="/cours/assets/tp1-ressources.IAiDkmcE.png",h=JSON.parse('{"title":"TP1 — Collaboration et coédition","description":"Partage OneDrive, gestion de fichiers, coédition Word (révision/commentaires) et compréhension du plagiat.","frontmatter":{"title":"TP1 — Collaboration et coédition","description":"Partage OneDrive, gestion de fichiers, coédition Word (révision/commentaires) et compréhension du plagiat."},"headers":[],"relativePath":"travaux/tp1.md","filePath":"travaux/tp1.md"}'),n={name:"travaux/tp1.md"};function l(c,e,d,u,p,g){return i(),t("div",null,[...e[0]||(e[0]=[o('<div class="flex flex-col gap-2 sm:flex-row sm:items-end sm:justify-between"><div><h1 class="m-0">TP1 — Collaboration et coédition</h1><p class="m-0 text-sm opacity-80">420-06A — Milieu de l’informatique</p></div><div class="text-sm opacity-80"><div><strong>Équipe :</strong> 3 à 4 étudiants</div><div><strong>Remise :</strong> dépôt sur Léa (voir consignes)</div></div></div><hr><h2 id="mise-en-situation" tabindex="-1">Mise en situation <a class="header-anchor" href="#mise-en-situation" aria-label="Permalink to &quot;Mise en situation&quot;">​</a></h2><p>Vous faites partie d’une jeune équipe universitaire qui a mis en place un logiciel de détection de plagiat innovant destiné à améliorer l’intégrité académique dans les institutions d’enseignement supérieur. Récemment, vous avez décidé de lancer une nouvelle fonctionnalité qui détecte également <strong>l’autoplagiat</strong>.</p><p>Pour mettre en œuvre cette nouvelle fonctionnalité, vous devez tester votre système à grande échelle avec une grande variété de documents académiques. Votre première étape consiste à effectuer le travail préparatoire à votre projet.</p><hr><h2 id="objectifs-du-travail" tabindex="-1">Objectifs du travail <a class="header-anchor" href="#objectifs-du-travail" aria-label="Permalink to &quot;Objectifs du travail&quot;">​</a></h2><ol><li>Valider vos installations du premier cours</li><li>Mettre en pratique la <strong>gestion de fichiers informatiques</strong></li><li>Démontrer vos compétences en <strong>coédition</strong> et <strong>collaboration</strong> avec M365</li><li>Valider votre compréhension du <strong>plagiat</strong> et de l’<strong>autoplagiat</strong></li></ol><div class="info custom-block"><p class="custom-block-title">INFO</p><ul><li>Ce travail se réalise en <strong>équipe de 3 à 4</strong>. Choisissez un <strong>responsable d’équipe</strong>.</li><li><a href="https://livecegepfxgqc-my.sharepoint.com/:x:/g/personal/otremblay_cegepgarneau_ca/IQAvf_qftUIQS7c759lhnDgbATnT41clfv4RMZQBOutJGFw?e=1P4kXk" target="_blank" rel="noreferrer">Formation des équipes GROUPE 1</a></li><li><a href="https://livecegepfxgqc-my.sharepoint.com/:x:/g/personal/otremblay_cegepgarneau_ca/IQAKDA7_-ptNRaliAc1eEfwhAZSEAPsPbhlJ4tLNj0EVPac?e=dnexwh" target="_blank" rel="noreferrer">Formation des équipes GROUPE 2</a></li></ul><p><strong>Les équipes peuvent être formées/complétées par le professeur.</strong></p></div><hr><h2 id="consignes" tabindex="-1">Consignes <a class="header-anchor" href="#consignes" aria-label="Permalink to &quot;Consignes&quot;">​</a></h2><h2 id="_1-preparation-word" tabindex="-1">1) Préparation (Word) <a class="header-anchor" href="#_1-preparation-word" aria-label="Permalink to &quot;1) Préparation (Word)&quot;">​</a></h2><h3 id="a-faire-individuellement" tabindex="-1">À faire individuellement <a class="header-anchor" href="#a-faire-individuellement" aria-label="Permalink to &quot;À faire individuellement&quot;">​</a></h3><ol><li>Créez un document Word nommé <strong><code>Informations_sur_plagiat.docx</code></strong>.</li><li>Rendez-vous à la page Web <strong><a href="https://www.cegepgarneau.ca/cegep/plagiat" target="_blank" rel="noreferrer">https://www.cegepgarneau.ca/cegep/plagiat</a></strong> et <strong>copiez/collez tout le texte des éléments interactifs</strong> de la page dans ce document. <ul><li>Attention : incluez aussi le texte de chaque <strong>élément interactif</strong>.</li></ul></li><li>Lisez le texte et : <ul><li>surlignez les mots importants;</li><li>ajoutez des notes sous forme de <strong>commentaires</strong>.</li></ul></li><li>Créez un <strong>nouveau document Word</strong> nommé <strong><code>Résumé_de_lecture.docx</code></strong>.</li><li>Rédigez une <strong>liste à puces</strong> des informations pertinentes retenues. <ul><li>Vous êtes évalués sur la <strong>pertinence</strong> de votre résumé.</li></ul></li></ol><h3 id="en-collaboration" tabindex="-1">En collaboration <a class="header-anchor" href="#en-collaboration" aria-label="Permalink to &quot;En collaboration&quot;">​</a></h3><ol start="6"><li>Dans son OneDrive, le <strong>responsable d’équipe</strong> crée l’arborescence suivante :<br><strong><code>420-06A – Milieu de l’informatique &gt; Travaux &gt; TP1</code></strong></li><li>Pour chaque coéquipier, créez un dossier au nom du coéquipier selon le format :<br><strong><code>NOM_Prenom</code></strong></li><li>Dans le dossier de chaque coéquipier, placez : <ul><li><code>Informations_sur_plagiat.docx</code></li><li><code>Résumé_de_lecture.docx</code></li></ul></li><li>Faites un <strong>retour d’équipe</strong> pour comparer vos résumés.</li><li>Produisez un document <strong><code>Résumé_commun.docx</code></strong> (résumé épuré des échanges) et placez-le :</li></ol><ul><li>dans un dossier qui sera <strong>toujours gardé en bas</strong> du dossier <strong>TP1</strong>.</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Astuce : pour garder un dossier « en bas », utilisez le principe de numérotation des dossiers vu en cours <strong>(01_Contenus, 02_Travaux, ..., 99_Archives)</strong>.</p></div><hr><h2 id="_2-developpement" tabindex="-1">2) Développement <a class="header-anchor" href="#_2-developpement" aria-label="Permalink to &quot;2) Développement&quot;">​</a></h2><h3 id="partie-1-—-dossiers-collaboratifs-dans-onedrive-structure-seulement" tabindex="-1">Partie 1 — Dossiers collaboratifs dans OneDrive (structure seulement) <a class="header-anchor" href="#partie-1-—-dossiers-collaboratifs-dans-onedrive-structure-seulement" aria-label="Permalink to &quot;Partie 1 — Dossiers collaboratifs dans OneDrive (structure seulement)&quot;">​</a></h3><p>**Chaque membre doit télécharger les fichiers sources: <a href="./../public/tps/tp1-sources.zip" target="_blank" rel="noopener">Fichiers sources à télécharger (.zip)</a></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Les fichiers fournis sont dans le désordre. C’est volontaire. C’est votre travail de les remettre en ordre en manipulant des accès différents. Voir Partie 2 du présent travail.</p></div><p>Dans les fichiers sources fournis, chaque coéquipier possède un ensemble de fichiers à placer dans une arborescence que vous devez créer.</p><blockquote><p>⚠️ <strong>Pour l’instant, vous ne placez aucun fichier</strong> : vous créez seulement l’arborescence de dossiers.</p></blockquote><ol><li>Créez un dossier <strong><code>Études de cas</code></strong> à la racine de <strong>TP1</strong>. <ul><li>Il contiendra plusieurs études de cas (une par personne).</li><li>La structure d’une étude de cas est montrée dans l’image fournie.</li><li>Vous pouvez ajuster les noms, mais gardez <strong>l’ordre</strong> des éléments.</li><li>Prévoyez qu’il pourrait y avoir <strong>plus de 100</strong> études de cas.</li></ul></li><li>Créez un dossier <strong><code>Ressources</code></strong> (un seul pour toute l’équipe) à la racine de <strong>TP1</strong>. <ul><li>L’ordre des sous-dossiers n’est pas important.</li></ul></li><li>Dans <strong>Études de cas</strong>, créez un <strong><code>Modèle de dossier</code></strong>. <ul><li>Ce dossier doit se classer complètement <strong>en haut</strong>.</li></ul></li><li><strong>Astuce</strong> Faites une <strong>carte heuristique</strong> pour planifier votre classement. <ul><li>Whiteboard M365 ou tableau en classe + photo (<a href="https://www.mindmaps.app/" target="_blank" rel="noreferrer">Essayez cet outil pratique</a>).</li></ul></li></ol><div class="flex flex-wrap md:flex-nowrap gap-4 items-start"><img src="'+r+'" alt="etude de cas" class="img-bordered w-auto max-w-full md:max-w-[48%] h-auto object-contain"><img src="'+a+'" alt="ressources" class="img-bordered w-auto max-w-full md:max-w-[48%] h-auto object-contain"></div><hr><h3 id="partie-2-—-onedrive-partage-depot-des-fichiers" tabindex="-1">Partie 2 — OneDrive (partage + dépôt des fichiers) <a class="header-anchor" href="#partie-2-—-onedrive-partage-depot-des-fichiers" aria-label="Permalink to &quot;Partie 2 — OneDrive (partage + dépôt des fichiers)&quot;">​</a></h3><ol><li>Le responsable partage le sous-dossier <strong><code>TP1</code></strong> à ses collègues <strong>en lecture seule</strong> (sans modification).</li><li>Le responsable crée la structure de la Partie 1 dans son OneDrive à l’intérieur de <strong>TP1</strong>.</li><li>Le responsable crée une étude de cas par coéquipier (en utilisant le modèle) : <ul><li>ex. <strong><code>Étude de cas 2</code></strong>, <strong><code>Étude de cas 3</code></strong>, etc.</li><li>Puis il donne les droits <strong>de modification</strong> individuellement : <ul><li>Étude de cas 2 → coéquipier 2</li><li>Étude de cas 3 → coéquipier 3</li></ul></li></ul></li><li>Chaque coéquipier télécharge le fichier <code>.zip</code> mentionné plus haut et place ses fichiers au bon endroit : <ul><li>certains dans <strong>Ressources</strong></li><li>d’autres dans <strong>Études de cas</strong></li></ul></li></ol><p>Règles de classement :</p><ul><li>Pour les fichiers contenant des <strong>dates</strong>, assurez-vous qu’elles soient en <strong>ordre chronologique</strong> (renommage léger permis).</li><li>Pour les fichiers avec un <strong>ordre logique</strong> (ex. <code>premier.docx</code>, <code>deuxième.docx</code>), assurez-vous que la séquence est respectée (renommage léger permis).</li></ul><hr><h3 id="partie-3-—-coedition-et-revision-dans-word" tabindex="-1">Partie 3 — Coédition et révision dans Word <a class="header-anchor" href="#partie-3-—-coedition-et-revision-dans-word" aria-label="Permalink to &quot;Partie 3 — Coédition et révision dans Word&quot;">​</a></h3><p>Les responsable doit télécharger le document Word suivant: <a href="./../public/tps/tp1-coédition.docx" target="_blank" rel="noopener">Document à télécharger (.docx)</a></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>N’acceptez pas les insertions dans l’onglet «Accepter» les marques de révision. Ceci me permettra de voir qui a fait l’insertion de chaque texte.</p></div><ol><li>Le responsable dépose une copie de ces consignes en format Word dans le dossier qui se classe automatiquement <strong>en bas</strong> du dossier TP1.</li><li>Il partage le document en <strong>mode coédition</strong> à ses collègues.<br><em>(Le dossier TP1 est déjà partagé en lecture seule; les droits d’écriture doivent être au niveau du <strong>document</strong> seulement.)</em></li><li>Chaque coéquipier ouvre le document en mode <strong>Révision</strong> et fait les tâches suivantes : <ol><li>À la première page, écrire son nom.</li><li>Répondre au questionnaire plus bas (répartissez-vous les questions et révisez les réponses des autres).</li><li>Ajouter un commentaire avec notification (ex. <code>@collègue1</code> et <code>@Olivier Tremblay</code>).</li><li>Répondre à un commentaire.</li><li>Modifier un mot ou une phrase d’un autre coéquipier.</li><li>Supprimer un mot ou une phrase.</li></ol></li><li>Finalisation : <ul><li>Le responsable partage le <strong>dossier racine TP1</strong> avec droit d’écriture <strong>au professeur uniquement</strong> (les collègues restent en lecture seule).</li><li>Il insère le lien sur le texte prévu à cet effet (ne pas afficher l’URL complète).</li></ul></li></ol><hr><h2 id="modalites-de-remise" tabindex="-1">Modalités de remise <a class="header-anchor" href="#modalites-de-remise" aria-label="Permalink to &quot;Modalités de remise&quot;">​</a></h2><h3 id="date-et-ponderation" tabindex="-1">Date et pondération <a class="header-anchor" href="#date-et-ponderation" aria-label="Permalink to &quot;Date et pondération&quot;">​</a></h3><p>La date de remise est indiquée dans <strong>Léa</strong>.</p><h3 id="fichier-s-et-espace-de-depot" tabindex="-1">Fichier(s) et espace de dépôt <a class="header-anchor" href="#fichier-s-et-espace-de-depot" aria-label="Permalink to &quot;Fichier(s) et espace de dépôt&quot;">​</a></h3><p>Le dossier racine <strong>TP1</strong> doit être <strong>compressé</strong> et déposé sur <strong>Léa</strong> par le responsable d’équipe uniquement.</p><hr><h2 id="liste-de-verification" tabindex="-1">Liste de vérification <a class="header-anchor" href="#liste-de-verification" aria-label="Permalink to &quot;Liste de vérification&quot;">​</a></h2><ul class="checklist"><li><label><input type="checkbox"><span class="check-text">Il y a un dossier classé complètement en bas du dossier racine et il contient :</span></label><ul class="checklist"><li><label><input type="checkbox"><span class="check-text">`Résumé_commun.docx`</span></label></li><li><label><input type="checkbox"><span class="check-text">Le document Word de coédition (copie coéditée)</span></label></li></ul></li><li><label><input type="checkbox"><span class="check-text">Il y a un dossier pour chacun des coéquipiers contenant :</span></label><ul class="checklist"><li><label><input type="checkbox"><span class="check-text">`Informations_sur_plagiat.docx`</span></label></li><li><label><input type="checkbox"><span class="check-text">`Résumé_de_lecture.docx`</span></label></li></ul></li><li><label><input type="checkbox"><span class="check-text">Il y a un dossier `Ressources`</span></label></li><li><label><input type="checkbox"><span class="check-text">Il y a un dossier `Études de cas`</span></label><ul class="checklist"><li><label><input type="checkbox"><span class="check-text">Il contient un `Modèle de dossier` (en haut)</span></label></li><li><label><input type="checkbox"><span class="check-text">Il contient une étude de cas par coéquipier</span></label></li></ul></li><li><label><input type="checkbox"><span class="check-text">Les fichiers du `.zip` sont dézippés, classés et renommés correctement</span></label></li><li><label><input type="checkbox"><span class="check-text">Le tableau d’identification d&#39;équipe est complété en coédition</span></label></li><li><label><input type="checkbox"><span class="check-text">Le dossier racine TP1 est compressé et déposé dans Léa</span></label></li></ul><hr>',47)])])}const b=s(n,[["render",l]]);export{h as __pageData,b as default};
