import{_ as i,c as a,o as l,ag as r}from"./chunks/framework.CmkU-fWO.js";const m=JSON.parse('{"title":"TP2 — Outil de budget personnel","description":"","frontmatter":{"title":"TP2 — Outil de budget personnel","aside":false},"headers":[],"relativePath":"travaux/tp2-excel-budget.md","filePath":"travaux/tp2-excel-budget.md"}'),t={name:"travaux/tp2-excel-budget.md"};function s(n,e,o,u,d,c){return l(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="tp2-—-outil-de-budget-personnel-dans-excel" tabindex="-1">TP2 — Outil de budget personnel dans Excel <a class="header-anchor" href="#tp2-—-outil-de-budget-personnel-dans-excel" aria-label="Permalink to &quot;TP2 — Outil de budget personnel dans Excel&quot;">​</a></h1><h2 id="objectif" tabindex="-1">Objectif <a class="header-anchor" href="#objectif" aria-label="Permalink to &quot;Objectif&quot;">​</a></h2><p>Créer un <strong>classeur Excel de budget personnel</strong> utilisant correctement les fonctionnalités vues dans le module.</p><hr><h2 id="remise" tabindex="-1">Remise <a class="header-anchor" href="#remise" aria-label="Permalink to &quot;Remise&quot;">​</a></h2><ul><li>un fichier Excel (.xlsx) contenant toutes les feuilles demandées ci-bas</li><li><strong>Date limite :</strong> Indiquée dans Léa</li><li><strong>Plateforme :</strong> Léa — TP1</li><li>-10% par jour de retard, max trois jours</li></ul><hr><h2 id="format" tabindex="-1">Format <a class="header-anchor" href="#format" aria-label="Permalink to &quot;Format&quot;">​</a></h2><ul><li>Classeur Excel (.xlsx)</li><li>Calculs automatisés (formules / fonctions)</li><li>Aucune valeur calculée manuellement</li><li>Les feuilles doivent présenter une mise en forme soignée et cohérente.</li><li>Les informations importantes doivent être faciles à repérer.</li><li>Les couleurs, bordures et alignements doivent être utilisés de façon pertinente.</li></ul><hr><h2 id="structure-obligatoire-du-classeur" tabindex="-1">Structure obligatoire du classeur <a class="header-anchor" href="#structure-obligatoire-du-classeur" aria-label="Permalink to &quot;Structure obligatoire du classeur&quot;">​</a></h2><p>Le classeur doit contenir <strong>les feuilles suivantes</strong>, nommées clairement.</p><hr><h2 id="_1️⃣-feuille-«-parametres-»" tabindex="-1">1️⃣ Feuille « Paramètres » <a class="header-anchor" href="#_1️⃣-feuille-«-parametres-»" aria-label="Permalink to &quot;1️⃣ Feuille « Paramètres »&quot;">​</a></h2><h3 id="requis" tabindex="-1">Requis <a class="header-anchor" href="#requis" aria-label="Permalink to &quot;Requis&quot;">​</a></h3><ul><li>Année analysée (pour information seulement)</li><li>Mois analysé (sera référencé ailleurs)</li><li>Catégories de dépenses (minimum 4) <ul><li>Exemples non imposés: Logement, Alimentation, Transport, Loisirs, Autres</li></ul></li></ul><h3 id="exigences" tabindex="-1">Exigences <a class="header-anchor" href="#exigences" aria-label="Permalink to &quot;Exigences&quot;">​</a></h3><ul><li>Les catégories doivent être utilisées ailleurs dans le classeur sous forme de liste déroulante.</li><li>Une validation des données sous forme de liste déroulante doit être utilisée.</li><li>Les paramètres doivent être référencés à l’aide de références absolues.</li><li>La mise en forme doit être claire et lisible.</li></ul><hr><h2 id="_2️⃣-feuille-«-transactions-»" tabindex="-1">2️⃣ Feuille « Transactions » <a class="header-anchor" href="#_2️⃣-feuille-«-transactions-»" aria-label="Permalink to &quot;2️⃣ Feuille « Transactions »&quot;">​</a></h2><h3 id="requis-1" tabindex="-1">Requis <a class="header-anchor" href="#requis-1" aria-label="Permalink to &quot;Requis&quot;">​</a></h3><ul><li>Date</li><li>Description</li><li>Catégorie</li><li>Type (Revenu / Dépense)</li><li>Montant</li></ul><h3 id="exigences-1" tabindex="-1">Exigences <a class="header-anchor" href="#exigences-1" aria-label="Permalink to &quot;Exigences&quot;">​</a></h3><ul><li>Les types de données doivent être appropriés pour chaque colonne.</li><li>La catégorie doit être sélectionnable à l’aide d’une liste déroulante.</li><li>Le type doit être limité aux valeurs <em>Revenu</em> ou <em>Dépense</em>.</li><li>La structure doit permettre l’ajout de nouvelles transactions sans briser les calculs.</li></ul><hr><h2 id="_3️⃣-feuille-«-calculs-»" tabindex="-1">3️⃣ Feuille « Calculs » <a class="header-anchor" href="#_3️⃣-feuille-«-calculs-»" aria-label="Permalink to &quot;3️⃣ Feuille « Calculs »&quot;">​</a></h2><h3 id="requis-2" tabindex="-1">Requis <a class="header-anchor" href="#requis-2" aria-label="Permalink to &quot;Requis&quot;">​</a></h3><ul><li>Total des revenus du mois analysé</li><li>Total des dépenses du mois analysé</li><li>Solde du mois analysé (doit avoir une mise en forme conditionnelle)</li><li>Moyenne des dépenses du mois analysé</li></ul><h3 id="exigences-2" tabindex="-1">Exigences <a class="header-anchor" href="#exigences-2" aria-label="Permalink to &quot;Exigences&quot;">​</a></h3><ul><li>Les calculs doivent être entièrement automatisés à l’aide de fonctions vues en classe.</li><li>Les références relatives et absolues doivent être utilisées de façon appropriée.</li><li>Une mise en forme conditionnelle doit permettre d’identifier visuellement une situation problématique (solde négatif).</li></ul><hr><h2 id="_4️⃣-feuille-«-analyse-»" tabindex="-1">4️⃣ Feuille « Analyse » <a class="header-anchor" href="#_4️⃣-feuille-«-analyse-»" aria-label="Permalink to &quot;4️⃣ Feuille « Analyse »&quot;">​</a></h2><h3 id="requis-3" tabindex="-1">Requis <a class="header-anchor" href="#requis-3" aria-label="Permalink to &quot;Requis&quot;">​</a></h3><ul><li>Tableau croisé dynamique</li><li>Analyse des dépenses</li><li>Regroupement par catégorie</li></ul><h3 id="exigences-3" tabindex="-1">Exigences <a class="header-anchor" href="#exigences-3" aria-label="Permalink to &quot;Exigences&quot;">​</a></h3><ul><li>La feuille doit contenir <strong>au moins un tableau croisé dynamique</strong> créé à partir des données de la feuille <em>Transactions</em>.</li><li>Le tableau croisé dynamique doit présenter <strong>les dépenses regroupées par catégorie</strong>.</li><li>Le montant des dépenses doit être <strong>additionné automatiquement</strong> dans le tableau croisé dynamique.</li><li>Le tableau croisé dynamique doit permettre <strong>le filtrage des données</strong> (ex. : par type ou autre critère pertinent).</li><li>Les données sources doivent être structurées de façon à permettre une mise à jour correcte du tableau croisé dynamique.</li></ul><hr><h2 id="_5️⃣-feuille-«-graphiques-»" tabindex="-1">5️⃣ Feuille « Graphiques » <a class="header-anchor" href="#_5️⃣-feuille-«-graphiques-»" aria-label="Permalink to &quot;5️⃣ Feuille « Graphiques »&quot;">​</a></h2><h3 id="requis-4" tabindex="-1">Requis <a class="header-anchor" href="#requis-4" aria-label="Permalink to &quot;Requis&quot;">​</a></h3><ul><li>Graphique des dépenses par catégorie</li><li>Graphique comparant revenus et dépenses</li><li>Feuille prête à être imprimée</li></ul><h3 id="exigences-4" tabindex="-1">Exigences <a class="header-anchor" href="#exigences-4" aria-label="Permalink to &quot;Exigences&quot;">​</a></h3><ul><li>Le type de graphique doit être pertinent par rapport aux données.</li><li>La source de données utilisée doit être correcte et à jour.</li><li>Les graphiques doivent comporter des titres et des légendes.</li><li>Les graphiques doivent être lisibles à l’écran et à l’impression.</li><li>L’orientation et les marges doivent être ajustées correctement.</li><li>Une zone d’impression doit être définie.</li><li>Les informations essentielles doivent être visibles sur papier.</li></ul><hr><h2 id="evaluation" tabindex="-1">Évaluation <a class="header-anchor" href="#evaluation" aria-label="Permalink to &quot;Évaluation&quot;">​</a></h2><p>Le travail sera évalué à l’aide de la <strong>grille d’évaluation fournie</strong>, selon le niveau de maîtrise démontré pour chaque feuille.</p><hr>',46)])])}const p=i(t,[["render",s]]);export{m as __pageData,p as default};
